import { randomInt } from 'crypto';

import { Body, Controller, Get, Param, ParseIntPipe, Patch, Query } from '@nestjs/common';
import { ApiBearerAuth, ApiOkResponse, ApiTags } from '@nestjs/swagger';

import { GetUserResponse, GetUsersQueryParams, GetUsersResponse, UpdateUserBody } from './requests';

function createUser (userId: number, companyId: number = 1): GetUserResponse {
    return {
        id: userId,
        companyId,
        name: 'Иван',
        patronymic: 'Иванович',
        surname: 'Иванов',
        position: 'Менеджер',
        phoneNumber: '+70001234567',
        email: 'user@zerno-agregator.ru',
        // eslint-disable-next-line max-len
        profilePictureUrl: 'data:image/jpeg;base64,',
    };
}

@Controller()
@ApiTags('UserMock')
@ApiBearerAuth()
class UserMockController {
    @Get('mock/user/:userId')
    @ApiOkResponse({
        type: GetUserResponse,
        description: 'Данные о пользователе отправлены',
    })
    public async getUser (
        @Param('userId', ParseIntPipe)
        userId: number,
    ): Promise <GetUserResponse> {
        return createUser(userId);
    }

    @Get('mock/users')
    @ApiOkResponse({
        type: GetUsersResponse,
        description: 'Данные о пользователях отправлены',
    })
    public async getUsers (
        @Query()
        queryParams: GetUsersQueryParams,
    ): Promise <GetUsersResponse> {
        return {
            users: queryParams.companyIds.map(companyId => createUser(randomInt(0, 100), companyId)),
        };
    }

    @Patch('mock/user/:userId')
    @ApiOkResponse({
        description: 'Данные пользователя изменены',
    })
    public async updateUser (
        @Param('userId', ParseIntPipe)
        userId: number,

        @Body()
        body: UpdateUserBody,
    ): Promise <void> {
        if (userId || body) {
            return;
        }
    }
}

export { UserMockController };
